INSERT INTO "CMORvar" VALUES('19bdb4c2-81b1-11e6-92de-ac72891c3257','cod','Cloud Optical Depth','f7dcac96-562c-11e6-a2a4-ac72891c3257','db3d77eebc6dc2fbcab4e0f894e46037',NULL,NULL,NULL,1,'real','aerosol',NULL,'AERday','MIPtable::AERday','AERday','AERday ((isd.003))',NULL,'AERday','day',-1,'The optical thickness is the integral along the path of radiation of a volume scattering/absorption/attenuation coefficient. The radiative flux is reduced by a factor exp(-optical_thickness) on traversing the path. A coordinate variable of radiation_wavelength or radiation_frequency can be specified to indicate that the optical thickness applies at specific wavelengths or frequencies. The atmosphere optical thickness applies to radiation passing through the entire atmosphere. Cloud means the component of extinction owing to the presence of liquid or ice water particles. The specification of a physical process by the phrase due_to_process means that the quantity named is a  single term in a sum of terms which together compose the general quantity named by omitting the phrase.',NULL);
INSERT INTO "CMORvar" VALUES('19bf238e-81b1-11e6-92de-ac72891c3257','cod','Cloud Optical Depth','f7dcac96-562c-11e6-a2a4-ac72891c3257','db3d77eebc6dc2fbcab4e0f894e46037',NULL,NULL,NULL,1,'real','aerosol',NULL,'AERmon-oth','MIPtable::AERmon','AERmon','AERmon ((isd.003))',NULL,'AERmon-oth','mon',-1,'The optical thickness is the integral along the path of radiation of a volume scattering/absorption/attenuation coefficient. The radiative flux is reduced by a factor exp(-optical_thickness) on traversing the path. A coordinate variable of radiation_wavelength or radiation_frequency can be specified to indicate that the optical thickness applies at specific wavelengths or frequencies. The atmosphere optical thickness applies to radiation passing through the entire atmosphere. Cloud means the component of extinction owing to the presence of liquid or ice water particles. The specification of a physical process by the phrase due_to_process means that the quantity named is a  single term in a sum of terms which together compose the general quantity named by omitting the phrase.',NULL);
