INSERT INTO "CMORvar" VALUES('6f36d5e0-9acb-11e6-b7ee-ac72891c3257','conccn','Aerosol Number Concentration','62eebe98-1617-11e7-a126-5404a60d96b5','70b0b8239a6ffb48b4a4f3086da12150',NULL,NULL,NULL,1,'real','atmos',NULL,NULL,'MIPtable::Emon','Emon','GeoMIP [aeroGeo]',NULL,NULL,'mon',12,'Number concentration means the number of particles or other specified objects per unit volume. Aerosol means the system of suspended liquid or solid particles in air (except cloud droplets) and their carrier gas, the air itself. Ambient_aerosol means that the aerosol is measured or modelled at the ambient state of pressure, temperature and relative humidity that exists in its immediate environment. Ambient aerosol particles are aerosol particles that have taken up ambient water through hygroscopic growth. The extent of hygroscopic growth depends on the relative humidity and the composition of the particles.',NULL);
INSERT INTO "CMORvar" VALUES('6d5b0f08-a39e-11e6-8a8c-ac72891c3257','concdust','Concentration of Dust','62eebe98-1617-11e7-a126-5404a60d96b5','a9e338f20a1b85e4758c5b3cb0ad961c',NULL,NULL,NULL,1,'real','atmos',NULL,NULL,'MIPtable::Emon','Emon','PMIP [PMIP-aero-02]',NULL,NULL,'mon',29,'Mass concentration means mass per unit volume and is used in the construction mass_concentration_of_X_in_Y, where X is a material constituent of Y. A chemical species denoted by X may be described by a single term such as nitrogen or a phrase such as nox_expressed_as_nitrogen. Aerosol means the system of suspended liquid or solid particles in air (except cloud droplets) and their carrier gas, the air itself. Aerosol particles take up ambient water (a process known as hygroscopic growth) depending on the relative humidity and the composition of the particles. Dry aerosol particles means aerosol particles without any water uptake.',NULL);
