INSERT INTO "CMORvar" VALUES('baabf756-e5dd-11e5-8482-ac72891c3257','dispkevfo','Ocean Kinetic Energy Dissipation per Unit Area Due to Vertical Friction','e9f9aa6c-c1ce-11e6-8067-ac72891c3257','abb3f8b62cc0e93f4ef5487c41ef10cb',NULL,NULL,NULL,3,'real','ocean',NULL,NULL,'MIPtable::Oclim','Oclim','Oclim ((isd.003))',NULL,NULL,'monC',25,'Friction, leading to the dissipation of kinetic energy, arises in ocean models as a result of the viscosity of sea water.  Generally, the  lateral (xy) viscosity is given a large value to maintain the numerical stability of the model.  In contrast, the vertical viscosity is usually much smaller. The specification of a physical process by the phrase due_to_process means that the quantity named is a  single term in a sum of terms which together compose the general quantity  named by omitting the phrase.',NULL);
