INSERT INTO "CMORvar" VALUES('800a51aa-f906-11e6-a176-5404a60d96b5','fco2nat','Surface Carbon Mass Flux into the Atmosphere Due to Natural Sources [kgC m-2 s-1]','63c0a932-15f2-11e7-87e0-5404a60d96b5','299fb9d19040c4aa3862644286261ad2',NULL,NULL,NULL,1,'real','atmos',NULL,NULL,'MIPtable::CFsubhr','CFsubhr','CFsubhr ((isd.003))','This table includes the 2-D variables listed in the Amon spreadsheet, omitting, however, the daily maximum and minimum temperatures.  All variables should be reported as synoptic fields, not daily means.',NULL,'subhrPt',1057,'This is what the atmosphere sees (on its own grid).  This field should be equivalent to the combined natural fluxes of carbon (requested in the L_mon and O_mon tables) that account for natural exchanges between the atmosphere and land or ocean reservoirs (i.e., net ecosystem biospheric productivity, for land, and air to sea CO2 flux, for ocean.)',NULL);
INSERT INTO "CMORvar" VALUES('baaded68-e5dd-11e5-8482-ac72891c3257','fco2nat','Surface Carbon Mass Flux into the Atmosphere Due to Natural Sources [kgC m-2 s-1]','f7dcac96-562c-11e6-a2a4-ac72891c3257','299fb9d19040c4aa3862644286261ad2',NULL,NULL,NULL,1,'real','atmos',NULL,'Amon_2d','MIPtable::Amon','Amon','Amon ((isd.003))','Report from all simulations (both emission-driven and concentration-driven) performed by models with fully interactive and responsive carbon cycles.','Amon_2d','mon',57,'This is what the atmosphere sees (on its own grid).  This field should be equivalent to the combined natural fluxes of carbon (requested in the L_mon and O_mon tables) that account for natural exchanges between the atmosphere and land or ocean reservoirs (i.e., net ecosystem biospheric productivity, for land, and air to sea CO2 flux, for ocean.)','Amon_2d');
