INSERT INTO "CMORvar" VALUES('e7073696-aa7f-11e6-9a4a-5404a60d96b5','fVegSoilMortality','Total Carbon Mass Flux from Vegetation to Soil as a Result of Mortality','f7de23aa-562c-11e6-a2a4-ac72891c3257','84ef402c-acb7-11e6-b5ee-ac72891c3257',NULL,NULL,NULL,2,'real','land',NULL,NULL,'MIPtable::Emon','Emon','C4MIP [C_LandT2]',NULL,NULL,'mon',41,'needed to separate changing vegetation C turnover times resulting from changing allocation versus changing mortality',NULL);
