INSERT INTO "CMORvar" VALUES('8b7fde2e-4a5b-11e6-9cd2-ac72891c3257','gpp','Carbon Mass Flux out of Atmosphere Due to Gross Primary Production on Land [kgC m-2 s-1]','f7de23aa-562c-11e6-a2a4-ac72891c3257','89c4bb4f45a0182fc00a1b86b13241a5',NULL,NULL,NULL,1,'real','land',NULL,NULL,'MIPtable::E3hr','E3hr','C4MIP [L_3hr]',NULL,NULL,'3hr',4,'only requested for DECK HIST, 1980-present',NULL);
INSERT INTO "CMORvar" VALUES('baae7800-e5dd-11e5-8482-ac72891c3257','gpp','Carbon Mass Flux out of Atmosphere Due to Gross Primary Production on Land [kgC m-2 s-1]','f7de23aa-562c-11e6-a2a4-ac72891c3257','89c4bb4f45a0182fc00a1b86b13241a5',NULL,NULL,NULL,1,'real','land',NULL,NULL,'MIPtable::Lmon','Lmon','Lmon ((isd.003))',NULL,NULL,'mon',38,'The rate of synthesis of biomass from inorganic precursors by autotrophs (producers) expressed as the mass of carbon which it contains. For example, photosynthesis in plants or phytoplankton. The producers also respire some of this biomass and the difference is referred to as the net primary production. ',NULL);
INSERT INTO "CMORvar" VALUES('8b7f90fe-4a5b-11e6-9cd2-ac72891c3257','gppc13','Carbon-13 Mass Flux out of Atmosphere Due to Gross Primary Production on Land [kgC m-2 s-1]','f7de23aa-562c-11e6-a2a4-ac72891c3257','590f9ace-9e49-11e5-803c-0d0b866b59f3',NULL,NULL,NULL,2,'real','land',NULL,NULL,'MIPtable::Emon','Emon','PMIP [PMIP-Lmon-02]',NULL,NULL,'mon',48,'The rate of synthesis of carbon-13 in biomass from inorganic precursors by autotrophs (producers) expressed as the mass of carbon which it contains. For example, photosynthesis in plants or phytoplankton. The producers also respire some of this biomass and the difference is referred to as the net primary production. ',NULL);
