INSERT INTO "CMORvar" VALUES('bcc6fc0a-b8a7-11e6-b7f0-ac72891c3257','nLitterSurf','Nitrogen Mass in Above-Ground Litter (non CWD)','f7de23aa-562c-11e6-a2a4-ac72891c3257','71c982aa-b8ab-11e6-97ab-ac72891c3257',NULL,NULL,NULL,2,'real','land',NULL,NULL,'MIPtable::Emon','Emon','C4MIP [C_LandT2]',NULL,NULL,'mon',77,'Content indicates a quantity per unit area. Litter is dead plant material in or above the soil. It is distinct from coarse wood debris. The precise distinction between fine and coarse is model dependent. Surface litter means the part of the litter resting above the soil surface. The sum of the quantities with standard names wood_debris_mass_content_of_nitrogen, surface_litter_mass_content_of_nitrogen and subsurface_litter_mass_content_of_nitrogen is the total nitrogen mass content of dead plant material.',NULL);
