INSERT INTO "CMORvar" VALUES('fda6b9c2-96ec-11e6-b81e-c9e268aff03a','noy','Total Reactive Nitrogen Volume Mixing Ratio','f7e94ce4-562c-11e6-a2a4-ac72891c3257','e29fbc42-b095-11e6-aab6-ac72891c3257',NULL,NULL,NULL,1,'real','aerosol',NULL,'aerzonal-vert','MIPtable::AERmonZ','AERmonZ','AERmonZ ((isd.003))',NULL,'aerzonal-vert','mon',-1,'Total family (the sum of all appropriate species in the model); list the species in the netCDF header, e.g. NOy = N + NO + NO2 + NO3 + HNO3 + 2N2O5 + HNO4 + ClONO2 + BrONO2 Definition: Total reactive nitrogen; usually includes atomic nitrogen (N), nitric oxide (NO), NO2, nitrogen trioxide (NO3), dinitrogen radical (N2O5), nitric acid (HNO3), peroxynitric acid (HNO4), BrONO2, ClONO2 add comment attribute with detailed description about how the model calculates these fields',NULL);
INSERT INTO "CMORvar" VALUES('bab26690-e5dd-11e5-8482-ac72891c3257','npp','Net Primary Production on Land as Carbon Mass Flux [kgC m-2 s-1]','f7de23aa-562c-11e6-a2a4-ac72891c3257','b3267e6a8cd7e4a5401e7fbca2c4bf5a',NULL,NULL,NULL,1,'real','land','down',NULL,'MIPtable::Lmon','Lmon','Lmon ((isd.003))','needed for any model that does not compute GPP',NULL,'mon',40,'Production of carbon means the production of biomass expressed as the mass of carbon which it contains. Net primary production is the excess of gross primary production (rate of synthesis of biomass from inorganic precursors) by autotrophs (producers), for example, photosynthesis in plants or phytoplankton, over the rate at which the autotrophs themselves respire some of this biomass. Productivity means production per unit area. The phrase expressed_as is used in the construction A_expressed_as_B, where B is a chemical constituent of A. It means that the quantity indicated by the standard name is calculated solely with respect to the B contained in A, neglecting all other chemical constituents of A.',NULL);
