INSERT INTO "CMORvar" VALUES('ba9e1460-e5dd-11e5-8482-ac72891c3257','o2','Dissolved Oxygen Concentration','f7e4000e-562c-11e6-a2a4-ac72891c3257','c2705ac5fb7561a3aa5744c1163bf2d7',NULL,NULL,NULL,1,'real','ocnBgchem',NULL,'Oyr_3dtr','MIPtable::Oyr','Oyr','Oyr ((isd.003))',NULL,'Oyr_3dtr','yr',29,'''Mole concentration'' means number of moles per unit volume, also called molarity, and is used in the construction mole_concentration_of_X_in_Y, where X is a material constituent of Y.  A chemical or biological species denoted by X may be described by a single term such as ''nitrogen'' or a phrase such as ''nox_expressed_as_nitrogen''.','Oyr_3dtr');
