INSERT INTO "CMORvar" VALUES('8b9e3c2a-4a5b-11e6-9cd2-ac72891c3257','rsdoabsorb','Net Rate of Absorption of Shortwave Energy in Ocean Layer','f7e4000e-562c-11e6-a2a4-ac72891c3257','b2f82090-fbed-11e5-8f03-5404a60d96b5',NULL,NULL,NULL,2,'real','ocean',NULL,NULL,'MIPtable::Emon','Emon','FAFMIP [fafOmonB]',NULL,NULL,'mon',62,'shortwave means shortwave radiation. Layer means any layer with upper and lower boundaries that have constant values in some vertical coordinate. There must be a vertical coordinate variable indicating the extent of the layer(s). If the layers are model layers, the vertical coordinate can be model_level_number, but it is recommended to specify a physical coordinate (in a scalar or auxiliary coordinate variable) as well. Net absorbed radiation is the difference between absorbed and emitted radiation.',NULL);
