INSERT INTO "CMORvar" VALUES('a955b11e-817c-11e6-a4e2-5404a60d96b5','tnhusd','Tendency of Specific Humidity Due to Numerical Diffusion','f7e790c0-562c-11e6-a2a4-ac72891c3257','2c8cb564bae033f641135194947da163',NULL,NULL,NULL,1,'real','atmos',NULL,NULL,'MIPtable::CFsubhr','CFsubhr','CFsubhr ((isd.003))',NULL,NULL,'subhrPt',43,'This includes any horizontal or vertical numerical moisture diffusion not associated with the parametrized moist physics or the resolved dynamics.  For example, any vertical diffusion which is part of the boundary layer mixing scheme should be excluded, as should any diffusion which is included in the terms from the resolved dynamics.   This term is required to check the closure of the moisture budget.',NULL);
INSERT INTO "CMORvar" VALUES('bab9d6c8-e5dd-11e5-8482-ac72891c3257','tnhusd','Tendency of Specific Humidity Due to Numerical Diffusion','62eebe98-1617-11e7-a126-5404a60d96b5','2c8cb564bae033f641135194947da163',NULL,NULL,NULL,1,'real','atmos',NULL,'CFmon_3dstd','MIPtable::CFmon','CFmon','CFmon ((isd.003))',NULL,'CFmon_3dstd','mon',35,'This includes any horizontal or vertical numerical moisture diffusion not associated with the parametrized moist physics or the resolved dynamics.  For example, any vertical diffusion which is part of the boundary layer mixing scheme should be excluded, as should any diffusion which is included in the terms from the resolved dynamics.   This term is required to check the closure of the moisture budget.','cfMon_3dstd');
INSERT INTO "CMORvar" VALUES('a955ca28-817c-11e6-a4e2-5404a60d96b5','tnhusmp','Tendency of Specific Humidity Due to Model Physics','f7e790c0-562c-11e6-a2a4-ac72891c3257','6e30ba1e2c19dcbd85faa176d4eae596',NULL,NULL,NULL,1,'real','atmos',NULL,NULL,'MIPtable::CFsubhr','CFsubhr','CFsubhr ((isd.003))',NULL,NULL,'subhrPt',45,'This includes sources and sinks from parametrized moist physics (e.g. convection, boundary layer, stratiform condensation/evaporation, etc.) and excludes sources and sinks from resolved dynamics or from horizontal or vertical numerical diffusion not associated with model physicsl.  For example any diffusive mixing by the boundary layer scheme would be included.',NULL);
