INSERT INTO "CMORvar" VALUES('baa4b4e6-e5dd-11e5-8482-ac72891c3257','tnpeo','Tendency of Ocean Potential Energy Content','e9f9f6ca-c1ce-11e6-8067-ac72891c3257','dfd869cd3463de6a57b2a9e10605efe7',NULL,NULL,NULL,3,'real','ocean',NULL,'Oyr_3dtr','MIPtable::Oyr','Oyr','Oyr ((isd.003))','Report on native horizontal grid.','Oyr_3dtr','yr',27,'Rate that work is done against vertical stratification, as measured by the vertical heat and salt diffusivity. Report here as depth integrated two-dimensional field.','Oyr_3dtr');
INSERT INTO "CMORvar" VALUES('bab9e8c0-e5dd-11e5-8482-ac72891c3257','tnpeo','Tendency of Ocean Potential Energy Content','e9f9aa6c-c1ce-11e6-8067-ac72891c3257','dfd869cd3463de6a57b2a9e10605efe7',NULL,NULL,NULL,3,'real','ocean',NULL,NULL,'MIPtable::Oclim','Oclim','Oclim ((isd.003))',NULL,NULL,'monC',18,'Rate that work is done against vertical stratification, as measured by the vertical heat and salt diffusivity. Report here as depth integrated two-dimensional field.',NULL);
INSERT INTO "CMORvar" VALUES('bab9eb4a-e5dd-11e5-8482-ac72891c3257','tnpeot','Tendency of Ocean Potential Energy Content Due to Tides','e9f9aa6c-c1ce-11e6-8067-ac72891c3257','1763f47c438dc252b1317c9861792f50',NULL,NULL,NULL,3,'real','ocean',NULL,NULL,'MIPtable::Oclim','Oclim','Oclim ((isd.003))',NULL,NULL,'monC',19,'Content indicates a quantity per unit area.  Potential energy is the sum of the gravitational potential energy relative to the geoid and the centripetal potential energy. (The geopotential is the specific potential energy.)  Due to tides means due to all astronomical gravity changes which manifest as tides.  No distinction is made between different tidal components. The specification of a physical process by the phrase due_to_process means that the quantity named is a  single term in a sum of terms which together compose the general quantity  named by omitting the phrase.  tendency_of_X means derivative of X with respect to time.',NULL);
INSERT INTO "CMORvar" VALUES('bab9edde-e5dd-11e5-8482-ac72891c3257','tnpeotb','Tendency of Ocean Potential Energy Content Due to Background','e9f9aa6c-c1ce-11e6-8067-ac72891c3257','d00cab8104f1a9e853ebfa511d725462',NULL,NULL,NULL,3,'real','ocean',NULL,NULL,'MIPtable::Oclim','Oclim','Oclim ((isd.003))',NULL,NULL,'monC',20,'Content indicates a quantity per unit area.  Potential energy is the sum of the gravitational potential energy relative to the geoid and the centripetal potential energy. (The geopotential is the specific potential energy.)  Due to background means caused by a time invariant imposed field which may be either constant over the globe or spatially varying, depending on the ocean model used. The specification of a physical process by the phrase due_to_process means that the quantity named is a  single term in a sum of terms which together compose the general quantity  named by omitting the phrase.  tendency_of_X means derivative of X with respect to time.',NULL);
