INSERT INTO "standardname" VALUES('mole_concentration_of_nitrate_in_sea_water','MoleConcentrationOfNitrateInSeaWater','Mole Concentration of Nitrate in Sea Water','Mole concentration means moles (amount of substance) per unit volume and is used in the construction mole_concentration_of_X_in_Y, where X is a material constituent of Y.','mol m-3',5);
