INSERT INTO "standardname" VALUES('mole_fraction_of_methanol_in_air','MoleFractionOfMethanolInAir','Mole Fraction of Methanol in Air','Mole fraction is used in the construction mole_fraction_of_X_in_Y, whereX is a material constituent of Y.  A chemical species denoted by X may be described by a single term such as ''nitrogen'' or a phrase such as ''nox_expressed_as_nitrogen''. The chemicalformula for methanol is CH3OH.','1',12);
INSERT INTO "standardname" VALUES('mole_fraction_of_methlyglyoxal_in_air','MoleFractionOfMethlyglyoxalInAir','Mole Fraction of Methlyglyoxal in Air','Mole fraction is used in the construction mole_fraction_of_X_in_Y, whereX is a material constituent of Y. A chemical species denoted by X may be described by a single term such as ''nitrogen'' or a phrase such as ''nox_expressed_as_nitrogen''. Methylglyoxal is an organic molecule with the chemical formula CH3COCHO. It is also called pyruvaldehyde or 2-oxopropanal.','1',19);
INSERT INTO "standardname" VALUES('mole_fraction_of_methyl_bromide_in_air','MoleFractionOfMethylBromideInAir','Mole Fraction of Methyl Bromide in Air','Mole fraction is used in the construction mole_fraction_of_X_in_Y, where X is a material constituent of Y.  The chemical formula of methyl bromide is CH3Br.','1',8);
