INSERT INTO "standardname" VALUES('mole_fraction_of_methyl_hydroperoxide_in_air','MoleFractionOfMethylHydroperoxideInAir','Mole Fraction of Methyl Hydroperoxide in Air','Mole fraction is used in the construction mole_fraction_of_X_in_Y, where X is a material constituent of Y.  The chemical formula of methyl hydroperoxide is CH3OOH.','1',8);
INSERT INTO "standardname" VALUES('mole_fraction_of_methyl_peroxy_radical_in_air','MoleFractionOfMethylPeroxyRadicalInAir','Mole Fraction of Methyl Peroxy Radical in Air','Mole fraction is used in the construction mole_fraction_of_X_in_Y, whereX is a material constituent of Y.  A chemical species denoted by X may be described by a single term such as ''nitrogen'' or a phrase such as ''nox_expressed_as_nitrogen''. The chemicalformula for methyl_peroxy_radical is CH3O2. In chemistry, a ''radical'' is a highly reactive, and therefore shortlived, species.','1',12);
INSERT INTO "standardname" VALUES('mole_fraction_of_molecular_hydrogen_in_air','MoleFractionOfMolecularHydrogenInAir','Mole Fraction of Molecular Hydrogen in Air','Mole fraction is used in the construction mole_fraction_of_X_in_Y, where X is a material constituent of Y.  The chemical formula of molecular hydrogen is H2.','1',8);
INSERT INTO "standardname" VALUES('mole_fraction_of_nitrate_radical_in_air','MoleFractionOfNitrateRadicalInAir','Mole Fraction of Nitrate Radical in Air','Mole fraction is used in the construction mole_fraction_of_X_in_Y, whereX is a material constituent of Y.  A chemical species denoted by X may be described by a single term such as ''nitrogen'' or a phrase such as ''nox_expressed_as_nitrogen''. In chemistry, a ''radical'' is a highly reactive, and therefore shortlived, species.','1',12);
INSERT INTO "standardname" VALUES('mole_fraction_of_nitric_acid_in_air','MoleFractionOfNitricAcidInAir','Mole Fraction of Nitric Acid in Air','Mole fraction is used in the construction mole_fraction_of_X_in_Y, where X is a material constituent of Y.','1',5);
INSERT INTO "standardname" VALUES('mole_fraction_of_nitric_acid_trihydrate_ambient_aerosol_particles_in_air','MoleFractionOfNitricAcidTrihydrateAmbientAerosolParticlesInAir','Mole Fraction of Nitric Acid Trihydrate Ambient Aerosol Particles in Air','Mole fraction is used in the construction mole_fraction_of_X_in_Y, where X is a material constituent of Y. A chemical species denoted by X may be described by a single term such as nitrogen or a phrase such as nox_expressed_as_nitrogen. Aerosol means the system of suspended liquid or solid particles in air (except cloud droplets) and their carrier gas, the air itself. Ambient_aerosol means that the aerosol is measured or modelled at the ambient state of pressure, temperature and relative humidity that exists in its immediate environment. Ambient aerosol particles are aerosol particles that have taken up ambient water through hygroscopic growth. The extent of hygroscopic growth depends on the relative humidity and the composition of the particles. The chemical formula for nitric acid is HNO3. Nitric acid trihydrate, sometimes referred to as NAT, is a stable crystalline substance consisting of three molecules of water to one molecule of nitric acid.','1',28);
INSERT INTO "standardname" VALUES('mole_fraction_of_nitrogen_dioxide_in_air','MoleFractionOfNitrogenDioxideInAir','Mole Fraction of Nitrogen Dioxide in Air','Mole fraction is used in the construction mole_fraction_of_X_in_Y, where X is a material constituent of Y.','1',5);
