INSERT INTO "standardname" VALUES('mole_ratio_of_nitrate_to_phosphate_in_sea_water','MoleRatioOfNitrateToPhosphateInSeaWater','Mole Ratio of Nitrate to Phosphate in Sea Water','Mole ratio is used in the construction mole_ratio_of_X_to_Y_in_medium, where X and Y are both material constituents of the medium.  Medium can take anyof the values given in the Small scale medium section of the standard name Guidelines document. ratio_of_X_to_Y  means X/Y.  The chemical formula for the nitrate anion is NO3-. The chemical formula of the phosphate anion is PO4 with a charge of minus three.','1',12);
INSERT INTO "standardname" VALUES('moles_of_cfc11_per_unit_mass_in_sea_water','MolesOfCfc11PerUnitMassInSeaWater','Moles of Cfc11 Per Unit Mass in Sea Water','moles_of_X_per_unit_mass_inY is also called molality of X in Y, where X is a material constituent of Y.  A chemical species denoted by X may be described by a single term such as ''nitrogen'' or a phrase such as ''nox_expressed_as_nitrogen''. The chemical formula of CFC11 is CFCl3. The IUPAC name fof CFC11 is trichloro-fluoro-methane.','mol kg-1',13);
