INSERT INTO "var" VALUES('5d432c16b179052a4e94c63af356c67c','diabdrag','Tendency of Eastward Wind from Numerical Artefacts','tendency_of_eastward_wind_due_to_numerical_artefacts','m s-2','Other sub-grid scale/numerical zonal drag excluding that already provided for the parameterized orographic and non-orographic gravity waves. This would be used to calculate the total ''diabatic drag''. Contributions to this additional drag such Rayleigh friction and diffusion that can be calculated from the monthly mean wind fields should not be included, but details (e.g. coefficients) of the friction and/or diffusion used in the model should be provided separately.',NULL,NULL,'CCMI1_monthly','CCMI','fd71b06a-3468-11e6-ba71-5404a60d96b5');
