INSERT INTO "var" VALUES('517f72b8577df7e97ce2dea8f1143e94','dmc','Deep Convective Mass Flux','atmosphere_net_upward_deep_convective_mass_flux','kg m-2 s-1','The net mass flux  represents the difference between the updraft and downdraft components.   This is calculated as the convective mass flux divided by the area of the whole grid cell (not just the area of the cloud).',NULL,NULL,'CMIP5_CFmon','CMIP5','fd71526e-3468-11e6-ba71-5404a60d96b5');
INSERT INTO "var" VALUES('590d8fc2-9e49-11e5-803c-0d0b866b59f3','dmlt','Depth to Soil Thaw','depth_at_shallowest_isotherm_defined_by_soil_temperature','m','Depth from surface to the zero degree isotherm. Above this isotherm T > 0o, and below this line T < 0o. Missing if surface is frozen or if soil is unfrozen at all depths.',NULL,NULL,'CMIP6 endorsement [LS3MIP]','LS3MIP','fd719698-3468-11e6-ba71-5404a60d96b5');
INSERT INTO "var" VALUES('04f25e5e4c98a0a98575bc3d805bb03a','dms','Dimethyl Sulphide (DMS) Mole Fraction','mole_fraction_of_dimethyl_sulfide_in_air','mol mol-1','Mole fraction is used in the construction mole_fraction_of_X_in_Y, where X is a material constituent of Y.',NULL,NULL,'CMIP5,C4MIP,HighResMIP,LS3MIP','CMIP5','fd724b4c-3468-11e6-ba71-5404a60d96b5');
INSERT INTO "var" VALUES('bdce9878-233e-11e6-a788-5404a60d96b5','dmso','Mole Concentration of Dimethyl Sulphide in Sea Water','mole_concentration_of_dimethyl_sulfide_in_sea_water','mol m-3','Mole concentration of dimethyl sulphide in water',NULL,NULL,'CMIP6 endorsement [OMIP]','OMIP','fd723bf2-3468-11e6-ba71-5404a60d96b5');
INSERT INTO "var" VALUES('c96ec19e-c5f0-11e6-ac20-5404a60d96b5','dmsos','Surface Mole Concentration of Dimethyl Sulphide in Sea Water','mole_concentration_of_dimethyl_sulfide_in_sea_water','mol m-3','Mole concentration of dimethyl sulphide in water in the near surface layer',NULL,NULL,'CMIP6 Endorsement [OMIP]','OMIP','fd723bf2-3468-11e6-ba71-5404a60d96b5');
INSERT INTO "var" VALUES('011e37046b327b256ca0e6b5f8722699','dpco2','Delta CO2 Partial Pressure','surface_carbon_dioxide_partial_pressure_difference_between_sea_water_and_air','Pa','Difference in partial pressure of carbon dioxide between sea water and air.  The partial pressure of a dissolved gas in sea water is the partial pressure in air with which it would be in equilibrium. ',NULL,NULL,'CMIP5,C4MIP,LS3MIP,PMIP','CMIP5','fd709388-3468-11e6-ba71-5404a60d96b5');
INSERT INTO "var" VALUES('c97324c8-c5f0-11e6-ac20-5404a60d96b5','dpco2abio','Abiotic Delta Pco Partial Pressure','surface_carbon_dioxide_abiotic_analogue_partial_pressure_difference_between_sea_water_and_air','Pa','Difference in partial pressure of abiotic-analogue carbon dioxide between sea water and air.  The partial pressure of a dissolved gas in sea water is the partial pressure in air with which it would be in equilibrium. An abiotic analogue is used to simulate the effect on a modelled variable when biological effects on ocean carbon concentration and alkalinity are ignored.',NULL,NULL,'CMIP6 Endorsement [OMIP]','OMIP','fd709388-3468-11e6-ba71-5404a60d96b5');
INSERT INTO "var" VALUES('c97316d6-c5f0-11e6-ac20-5404a60d96b5','dpco2nat','Natural Delta CO2 Partial Pressure ','surface_carbon_dioxide_natural_analogue_partial_pressure_difference_between_sea_water_and_air','Pa','Difference in partial pressure of natural-analogue carbon dioxide between sea water and air.  The partial pressure of a dissolved gas in sea water is the partial pressure in air with which it would be in equilibrium.  A natural analogue is used to simulate the effect on a modelled variable of imposing preindustrial atmospheric carbon dioxide concentrations, even when the model as a whole may be subjected to varying forcings. ',NULL,NULL,'CMIP6 Endorsement [OMIP]','OMIP','fd709388-3468-11e6-ba71-5404a60d96b5');
INSERT INTO "var" VALUES('abe5993d07b8b52e770ed957b060f9ed','dpo2','Delta O2 Partial Pressure','surface_molecular_oxygen_partial_pressure_difference_between_sea_water_and_air','Pa','The partial pressure of a dissolved gas in sea water is the partial pressure in air with which it would be in equilibrium.  The partial pressure of a gaseous constituent of air is the pressure which it alone would exert with unchanged temperature and number of moles per unit volume.  The surface called surface means the lower boundary of the atmosphere.',NULL,NULL,'OMIP','OMIP','fd709388-3468-11e6-ba71-5404a60d96b5');
INSERT INTO "var" VALUES('591313de-9e49-11e5-803c-0d0b866b59f3','drivw','Change in River Storage','change_over_time_in_river_water_amount','kg m-2','Change over time of the mass of water per unit area in the fluvial system (stream and floodplain).',NULL,NULL,'CMIP6 endorsement [LS3MIP]','LS3MIP','fd711ac4-3468-11e6-ba71-5404a60d96b5');
INSERT INTO "var" VALUES('f17637206609c6f9e14190d3ac6a1e6b','drybc','Dry Deposition Rate of Black Carbon Aerosol Mass','minus_tendency_of_atmosphere_mass_content_of_elemental_carbon_dry_aerosol_particles_due_to_dry_deposition','kg m-2 s-1','Dry deposition includes gravitational settling, impact scavenging, and turbulent deposition.',NULL,NULL,'CCMI1_monthly','CCMI','fd71526e-3468-11e6-ba71-5404a60d96b5');
INSERT INTO "var" VALUES('6444d8c6e394c5d66ae3f732f0ede043','drydust','Dry Deposition Rate of Dust','minus_tendency_of_atmosphere_mass_content_of_dust_dry_aerosol_particles_due_to_dry_deposition','kg m-2 s-1','Dry deposition includes gravitational settling, impact scavenging, and turbulent deposition.',NULL,NULL,'CCMI1_monthly','CCMI','fd71526e-3468-11e6-ba71-5404a60d96b5');
