INSERT INTO "var" VALUES('41455e80-4f40-11e6-a814-ac72891c3257','mrroLi','Land Ice Runoff Flux','land_ice_runoff_flux','kg m-2 s-1','Runoff flux over land ice is the difference between any available liquid water in the snowpack less any refreezing. Computed as the sum of rainfall and melt of snow or ice less any refreezing or water retained in the snowpack',NULL,NULL,'CMIP6 endorsement [ISMIP6]','ISMIP6','fd71526e-3468-11e6-ba71-5404a60d96b5');
