INSERT INTO "var" VALUES('c2705ac5fb7561a3aa5744c1163bf2d7','o2','Dissolved Oxygen Concentration','mole_concentration_of_dissolved_molecular_oxygen_in_sea_water','mol m-3','''Mole concentration'' means number of moles per unit volume, also called molarity, and is used in the construction mole_concentration_of_X_in_Y, where X is a material constituent of Y.  A chemical or biological species denoted by X may be described by a single term such as ''nitrogen'' or a phrase such as ''nox_expressed_as_nitrogen''.',NULL,NULL,'OMIP','OMIP','fd723bf2-3468-11e6-ba71-5404a60d96b5');
INSERT INTO "var" VALUES('01f509c26cfbd2f30789d91b026e0016','o2min','Oxygen Minimum Concentration','mole_concentration_of_dissolved_molecular_oxygen_in_sea_water_at_shallowest_local_minimum_in_vertical_profile','mol m-3','''Mole concentration'' means number of moles per unit volume, also called molarity, and is used in the construction mole_concentration_of_X_in_Y, where X is a material constituent of Y.  A chemical or biological species denoted by X may be described by a single term such as ''nitrogen'' or a phrase such as ''nox_expressed_as_nitrogen''. The concentration of any chemical species, whether particulate or dissolved, may vary with depth in the ocean.  A depth profile may go through one or more local minima in concentration. The mole_concentration_of_molecular_oxygen_in_sea_water_at_shallowest_local_minimum_in_vertical_profile is the mole concentration of oxygen at the local minimum in the concentration profile that occurs closest to the sea surface.',NULL,NULL,'CMIP5,C4MIP,LS3MIP,PMIP','CMIP5','fd723bf2-3468-11e6-ba71-5404a60d96b5');
INSERT INTO "var" VALUES('c96d9daa-c5f0-11e6-ac20-5404a60d96b5','o2os','Surface Dissolved Oxygen Concentration','mole_concentration_of_dissolved_molecular_oxygen_in_sea_water','mol m-3','''Mole concentration'' means number of moles per unit volume, also called molarity, and is used in the construction mole_concentration_of_X_in_Y, where X is a material constituent of Y.  A chemical or biological species denoted by X may be described by a single term such as ''nitrogen'' or a phrase such as ''nox_expressed_as_nitrogen''.',NULL,NULL,'CMIP6 Endorsement [OMIP]','OMIP','fd723bf2-3468-11e6-ba71-5404a60d96b5');
INSERT INTO "var" VALUES('e4e75ce1fe85a547cd0d39d4b64ec0e2','o2sat','Dissolved Oxygen Concentration at Saturation','mole_concentration_of_dissolved_molecular_oxygen_in_sea_water_at_saturation','mol m-3','Mole concentration at saturation means the mole concentration in a saturated solution. Mole concentration means number of moles per unit volume, also called molarity, and is used in the construction mole_concentration_of_X_in_Y, where X is a material constituent of Y. A chemical or biological species denoted by X may be described by a single term such as nitrogen or a phrase such as nox_expressed_as_nitrogen.',NULL,NULL,'OMIP_Oyr','OMIP','fd723bf2-3468-11e6-ba71-5404a60d96b5');
