INSERT INTO "var" VALUES('31eb8d2a1ed4d82ef8bebe4227ec90b9','so','Sea Water Salinity','sea_water_salinity','0.001','Sea water salinity is the salt content of sea water, often on the Practical Salinity Scale of 1978. However, the unqualified term ''salinity'' is generic and does not necessarily imply any particular method of calculation. The units of salinity are dimensionless and the units attribute should normally be given as 1e-3 or 0.001 i.e. parts per thousand. ',NULL,NULL,'OMIP.Omon','OMIP','fd6fda9c-3468-11e6-ba71-5404a60d96b5');
