INSERT INTO "var" VALUES('28e774ec0ecd561a6a3d437e6c443a6b','zg','Geopotential Height','geopotential_height','m','Geopotential is the sum of the specific gravitational potential energy relative to the geoid and the specific centripetal potential energy. Geopotential height is the geopotential divided by the standard acceleration due to gravity. It is numerically similar to the altitude (or geometric height) and not to the quantity with standard name height, which is relative to the surface.',NULL,NULL,'CMIP5_Amon,CMIP5_CFday,CMIP5_CFsubhr,CMIP5_day,PMIP3_Aclim,PMIP3_Amon,PMIP3_day,SPECS_Amon,SPECS_day','CMIP5','fd719698-3468-11e6-ba71-5404a60d96b5');
